import { configureStore } from '@reduxjs/toolkit';
import { createAction, createReducer } from '@reduxjs/toolkit';

export const addContact = createAction('contacts/add');
export const deleteContact = createAction('contacts/delete');
export const addFilter = createAction('filter/add');

const initContactsState = JSON.parse(window.localStorage.getItem("contacts")) ?? [];

const contactsReducer = createReducer(initContactsState, {
    [addContact]: (state, action) => [...state, action.payload],
    [deleteContact]: (state, action) => state.filter(contact => contact.id !== action.payload),
});

const filterReducer = createReducer('', {
    [addFilter]: (state, action) => action.payload,
});


export const store = configureStore({
    reducer: {
        contacts: contactsReducer,
        filter: filterReducer,
    },
});
