import { createSlice } from '@reduxjs/toolkit';
import { combineReducers } from 'redux';
import { fetchContactsOperation, deleteContactOperation, addContactOperation } from './operations';

const contactsSlice = createSlice({
    name: 'contacts',
    initialState : [],
    reducers: {},
    extraReducers: {
        [fetchContactsOperation.fulfilled]: (_, action) => action.payload,
        [deleteContactOperation.fulfilled]: (state, action) => state.filter(contact => contact.id !== action.payload.id),
        [addContactOperation.fulfilled]: (state, action) => state.push(action.payload),
    }
});


const filterSlice = createSlice({
    name: 'filter',
    initialState: '',
    reducers: {
        addFilter(_, action) {
            return action.payload;
        },
    }
});



export const { addContact, deleteContact } = contactsSlice.actions;
export const { addFilter } = filterSlice.actions;

export const rootReducer = combineReducers({ items: contactsSlice.reducer, filter: filterSlice.reducer });
