import { createSlice } from '@reduxjs/toolkit';
import { fetchContactsOperation, deleteContactOperation, addContactOperation } from './contacts-operations';

const initialState = {
  items: [],
  filter: '',
  isLoading: false,
  error: null,
};

const contactsSlice = createSlice({
    name: 'contacts',
    initialState,
    reducers: {
        addFilter(state, action) {
            state.filter = action.payload
        },
    },
    extraReducers: {
        [fetchContactsOperation.fulfilled]: (state, action) => { state.items = [...action.payload]; state.isLoading = false },
        [deleteContactOperation.fulfilled]: (state, action) => { state.items = [...state.items.filter(contact => contact.id !== action.payload.id)]; state.isLoading = false },
        [addContactOperation.fulfilled]: (state, action) => { state.items = [...action.payload]; state.isLoading = false },
        [fetchContactsOperation.rejected]: (state, action) => { state.error = action; state.isLoading = false },
        [deleteContactOperation.rejected]: (state, action) => { state.error = action; state.isLoading = false },
        [addContactOperation.rejected]: (state, action) => { state.error = action; state.isLoading = false },
        [fetchContactsOperation.pending]: (state) => {state.isLoading = true},
        [deleteContactOperation.pending]: (state) => {state.isLoading = true},
        [addContactOperation.pending]: (state) => {state.isLoading = true},
    }
});

export const { addFilter } = contactsSlice.actions;

export const contactsReducer = contactsSlice.reducer;
