import { NavLink, useLocation } from 'react-router-dom';

import s from './AuthNav.module.css';

function AuthNav() {
    const location = useLocation();
    const isOnHome = location.pathname === '/';

    const classNameByLocation = className => {
        console.log(className);
        console.log(isOnHome);
        if (isOnHome) {
            return `${s[className]}OnHome`;
        }
        return `${s[className]}`;
    }


    return <nav className={classNameByLocation('registration')}>
        <NavLink className={classNameByLocation('signin')} to='/login'>Sign In</NavLink>
        <NavLink className={classNameByLocation('signup')} to='/register'>Sign Up</NavLink>
    </nav>
}
export default AuthNav;
