import { createSlice } from '@reduxjs/toolkit';
import { combineReducers } from 'redux';


const userSlice = createSlice({
    name: 'authUser',
    initialState : {name: null, email: null, token: null},
    reducers: {},
    extraReducers: {}
});

export const authReducer = userSlice.reducer;


