import { createSlice } from '@reduxjs/toolkit';
import { combineReducers } from 'redux';

const initialState = {
  user: { name: null, email: null },
  token: null,
  isLoggedIn: false,
  isFetchingCurrentUser: false,
};


const authSlice = createSlice({
    name: 'authUser',
    initialState,
    reducers: {},
    extraReducers: {}
});

export const authReducer = userSlice.reducer;


