import { createAsyncThunk } from "@reduxjs/toolkit";
import { getContacts } from '../api/api';

export const fetchContacts =  createAsyncThunk(
  'contacts/fetchContacts',
  async () => {
      const contacts = await getContacts();
    return contacts;
  }
);
